SYSTEM: OEG_and_IoT.pptx: Diapositiva {9}
SELECT (AVG(?tsky as ?avg) WHERE {
 {
   ?w wot:offersInteraction [
      rdfs:label “lat”;
      wot:hasLatestEntry [
         wot:value ?tlat
      ]
   ] .
   ?w wot:offersInteraction [
      rdfs:label “long”;
      wot:hasLatestEntry [
         wot:value ?tlong
      ]
   ] .
   ?w wot:offersInteraction [
      rdfs:label “tsky”;
      wot:hasLatestEntry [
         wot:value ?tsky
      ]
   ] .
 }
 {
   [] a sch:Place ;
      foaf:name ?name ;
      geo:lat ?lat ;
      geo:long ?long
 }
 FILTER(STR(?name)=“Madrid” && (?tlong-?long)*(?long-?tlong) < 1 && (?tlat-?lat)*(?lat-?tlat) < 1)
}
